import { useState } from "react";
import axios from "axios";

const Handles = () => {
  const [values, setValues] = useState();
  const [open, setOpen] = useState(false);

  const handleChangeValues = (value) => {
    setValues(previousValues => ({
      ...previousValues,
      [value.target.id]: value.target.value,
    }))
  };

  const handleSend = () => {
    axios.post("http://localhost:3001/api/patients", {
      first_name: values.firstName,
      last_name: values.lastName,
      age: values.age,
      gender: values.gender,
    }).then((response) => {
      console.log(response);
    });
  };

  const handlePopUp = (event) => {
    [values.target.id] = event.target.value;
    window.popup = document.getElementById("popup");
    window.popup.style.display = "block";
  };

  const togglePopup = () => {
    setOpen(!open);
  };

  return { values, handleChangeValues, handleSend, handlePopUp };
};

export default Handles;
