import { useState } from "react";
import axios from "axios";

const Handles = () => {
  const [values, setValues] = useState();
  const [editOpen, setEditOpen] = useState(false);
  const [deleteOpen, setDeleteOpen] = useState(false);

  const handleChangeValues = (value) => {
    setValues(previousValues => ({
      ...previousValues,
      [value.target.id]: value.target.value,
    }))
  };

  const handleSend = () => {
    axios.post("http://localhost:3001/api/patients", {
      first_name: values.firstName,
      last_name: values.lastName,
      age: values.age,
      gender: values.gender,
    }).then((response) => {
      console.log(response);
    });
  };

  const handleEdit = (click) => {
    [values.target.id] = click.target.id;
    setEditOpen(!editOpen);
    window.popup = document.getElementById("edit-popup");
    window.popup.style.display = "block";
  };

  const handleDelete = (click) => {
    [values.target.id] = click.target.id;
    setDeleteOpen(!deleteOpen);
    window.popup = document.getElementById("delet-popup");
    window.popup.style.display = "block";
  };

  return { values, handleChangeValues, handleSend, handleEdit, handleDelete };
};

export default Handles;
