import { useState } from "react";
import axios from "axios";

const Handles = () => {
  const [values, setValues] = useState();
  const [renderPopup, setRenderPopup] = useState(true);

  const handleChangeValues = (value) => {
    setValues(previousValues => ({
      ...previousValues,
      [value.target.name]: value.target.value,
    }))
  };

  const handleSend = () => {
    axios.post("http://localhost:3001/api/patients", {
      first_name: values.firstName,
      last_name: values.lastName,
      age: values.age,
      gender: values.gender,
    }).then((response) => {
      console.log(response);
    });
  };

  const togglePopup = () => {
    setRenderPopup(!renderPopup);
    console.log(renderPopup);
  };

  return { values, renderPopup, handleChangeValues, handleSend, togglePopup };
};

export default Handles;
